mapper.select:
select * FROM (SELECT rownum as rnum , shno,mememail,shteamname,shvideo,shname,shmapcoords,shmapaddr,shmapdetail,shdate,shtime,shtype,shdetail FROM show WHERE shdate=#{date}
and shTeamName LIKE '%'|| #{val} ||'%'
and shmapaddr LIKE '%' || #{val} || '%'
and shType = #{val}
and shType not in ('노래','마술','댄스','연주','마술','퍼포먼스')
ORDER BY shno DESC) WHERE rnum BETWEEN ((#{curPage}-1)*2)+1 AND #{curPage}*2

SELECT count(shNo) FROM show WHERE shdate=#{date}
and shTeamName LIKE '%'|| #{val} ||'%'
and shmapaddr LIKE '%' || #{val} || '%'
and shType = #{val}
and shType not in ('노래','마술','댄스','연주','마술','퍼포먼스')
