mapper.select:
SELECT memEmail, spName, spPhoto, spContent, spCond, spTel, spNo 
FROM sponser 
WHERE spNo=#{num}

SELECT spName, spContent, spCond, spTel, spNo, memEmail 
FROM sponser
WHERE spNo=#{spNo}

mapper.update:
UPDATE sponser  
SET spName=#{spName}, spPhoto=#{spPhoto}, spContent=#{spContent}, spCond=#{spCond}, spTel=#{spTel}
WHERE spNo=#{spNo}

mapper.select:
SELECT memvideo
FROM member
WHERE memEmail = #{memEmail}

SELECT backName, backVideo, backContent, backTel, memEmail, backNo
FROM backed
WHERE backNO=#{num}

SELECT backName, backVideo, backContent, backTel, backNo, memEmail
FROM backed
WHERE backNo=#{backNo}

mapper.update:
UPDATE backed
SET backName=#{backName}, backVideo=#{backVideo}, backContent=#{backContent}, backTel=#{backTel} 
WHERE backNo=#{backNo}
